my .S files:
.syntax unified
.align 4
.type add %function
.section .text
.global add

add:
ADD r0, r0, r1
BX lr

.syntax unified
.align 4
.type bitcounting %function
.section .text
.global bitcounting

bitcounting:

PUSH {R4, r5, LR}      @ Save registers and link register
MOV r5, #0x0	@counter

bitcount_loop:
CMP r0, #0x0
BEQ bitcount_end
AND r4, r0, #0x1	@extracting first bit in string, storing in r4
CMP r4, #0x1
BLEQ bitcount_increment @if r4=1, counter will be incremented.
LSR r0, r0, #0x1
B bitcount_loop

bitcount_increment:

ADD r5, r5, #0x1
BX lr

bitcount_end:

MOV r0, r5
POP {r4, r5, lr}
BX lr

.syntax unified
.align 4
.type exponentiation %function
.section .text
.global exponentiation

exponentiation:
MOV r0, #0x5
MOV r1, #0x5

CMP r0, #0x0		 @ Check if r0=0
BEQ exp_error_check
B exp_start

exp_error_check:
CMP r1, #0x0	@ Check if r1=0
BNE exp_start
MOV r0, #0xFFFFFFFF		@if 0^0 condition, error. returns -1
BX lr

exp_start:
PUSH {r2, sp, lr}	  @ To clear r2 once loop is finished
MOV r2, #0x1          @ Initialize result to 1
CMP r1, #0x0          @ Compare exponent to 0
BEQ exp_done          @ If exponent is 0, return 1

exp_loop:
MUL r2, r2, r0        @ Multiply result by base
SUB r1, r1, #1       @ Decrement exponent by 1
CMP r1, #0x0
BNE exp_loop          @ If exponent is not 0, continue loop

exp_done:
MOV r0, r2            @ Move result to r0 for return
POP {r2, sp, lr}	  @ Clear all registers
BX lr                 @ Return

.syntax unified
.align 4
.type factorial %function
.section .text
.global factorial

factorial:
CMP r0, #0x0
BEQ baseCase0
BL factorialHelper
POP {sp, lr}
BX LR

factorialHelper:
PUSH {r4, lr}
MOV r4, r0
CMP r0, #0x1
BEQ baseCase1
SUB r0, r0, #0x1
BL factorialHelper

baseCase1:
MUL r0, r0, r4
POP {r4, lr}
BX LR

baseCase0:
MOV r0, #0x1
BX LR

.syntax unified
.align 4
.type floordivision %function
.section .text
.global floordivision

floordivision:

cmp r1, #0            @ Compare divisor to 0
bne floordivstart    
MOV r0, #0xFFFFFFFF	 @ If divisor is 0, return -1
BX lr

floordivstart:
PUSH {r4, sp, lr}	  @ To clear registers after returning
MOV r4, #0x0    @ To store result

floor_div_loop:
cmp r0, r1            @ Compare dividend to divisor
blt floor_div_done    @ If dividend < divisor, break loop
sub r0, r0, r1        @ Subtract divisor from dividend
add r4, r4, #1        @ Increment quotient by 1
b floor_div_loop      @ Repeat loop

floor_div_done:
mov r0, r4            @ Move quotient to r0 for return
POP {r4, sp, lr}
bx lr                 @ Return

.syntax unified
.align 4
.type modulus %function
.section .text
.global modulus

modulus:
CMP r1, #0x0	@check if dividing by zero. return -1 if yes
BEQ modulus_error
B modulus_loop

modulus_error:
MOV r0, #0xFFFFFFFF
POP {sp, lr}
BX lr

modulus_loop:
CMP r0, r1	@if r0<r1
BLT modulus_end
SUB r0, r0, r1	@r0=r0-r1
B modulus_loop

modulus_end:
POP {sp, lr}
BX lr

.syntax unified
.align 4
.type multiply %function
.section .text
.global multiply

multiply:
MUL r0, r0, r1
BX lr

.syntax unified
.align 4
.type subtract %function
.section .text
.global subtract

subtract:
CMP r0, r1  @if r0<r1, swap the values
BLT subtract_swap
B subtract_start

subtract_swap:
PUSH {r4}
MOV r4, r0
MOV r0, r1
MOV r1, r4
POP {r4}
BX lr

subtract_start:
SUB r0, r0, r1
BX lr

.syntax unified
.align 4
.type summation %function
.section .text
.global summation

summation:

CMP r0, r1	@if r0>r1, swap
BGT sum_swap
BEQ sum_equal	@if r0==r1, return r0+r1
PUSH {r4, sp, lr}	@pushing register to clear them once result is returned
B sum_loop

sum_equal:
ADD r0, r0, r1
BX lr

sum_swap:
PUSH {r4}	@pushing temp r4 to clear it once swap is done
MOV r4, r0
MOV r0, r1
MOV r1, r4
POP {r4}
B summation

sum_loop:

ADD r4, r4, r0	@r4=r4+r0
ADD r0, #0x1	@r0++
CMP r0, r1	@if r0!=r1, loop
BLT sum_loop

ADD r4, r4, r1 @to add last number to result
MOV r0, r4
POP {r4, sp, lr}
BX lr

