fin.read(reinterpret_cast<char *>(&ftype), sizeof(ftype));

if (fin.eof()) {
break;
}

int32_t nelements = 1;
int32_t ne[2] = { 1, 1 };
for (int i = 0; i < n_dims; ++i) {
