}

auto tensor = model.tensors[name.data()];

if (ggml_nelements(tensor) != nelements) {
fprintf(stderr, “%s: tensor ‘%s’ has wrong size in model file\n”, func, name.data());
return false;
}
if (tensor->ne[0] != ne[0] || tensor->ne[1] != ne[1]) {
