.global modulus

modulus:
CMP r1, #0x0	@check if dividing by zero. return -1 if yes
BEQ modulus_error
B modulus_loop

modulus_error:
MOV r0, #0xFFFFFFFF
POP {lr}
BX lr

modulus_loop:
CMP r0, r1	@if r0<r1
BLT modulus_end
SUB r0, r0, r1	@r0=r0-r1
B modulus_loop

modulus_end:
POP {lr}
BX lr


